'use babel';
/* @flow */

var hyperclick: ?Hyperclick = null;

module.exports = {
  activate() {
    var Hyperclick = require('./Hyperclick');
    hyperclick = new Hyperclick();
  },

  deactivate() {
    if (hyperclick) {
      hyperclick.dispose();
      hyperclick = null;
    }
  },

  consumeProvider(provider: HyperclickProvider | Array<HyperclickProvider>): void {
    if (hyperclick) {
      hyperclick.consumeProvider(provider);
    }
  },
};
